from flask import Flask, render_template, request
import random
# master variable
app = Flask("__main__")


#routes --> different request me karna kya hai


my_age = 17

# my_intro = [my_name, my_age, my_hobby]
my_intro = {
	"name" : "Rohit",
	"age" : 19,
	"hobby" : "Singing"
}


dog = [
"https://i.ytimg.com/vi/7xh1DAKIdng/maxresdefault.jpg",
"data:image/jpeg;base64,",
"https://post.medicalnewstoday.com/wp-content/uploads/sites/3/2020/02/322868_1100-800x825.jpg"
]


@app.route("/")
def kuch_bhi():
	return render_template("home.html", intro = my_intro, age = my_age, dog_img = random.choice(dog)  )


@app.route("/orders", )
def order():
	return "I'm a order page"

@app.route("/submit", methods=['POST', 'GET'])
def submit():
	if request.method == 'POST':
		n = request.form['my_name']

		return render_template("home.html", intro = my_intro, age = my_age, dog_img = random.choice(dog), welcome = n  )


# Marks prediction
@app.route("/marks", methods=['POST'])
def marks():
	if request.method == 'POST':
		hours = request.form['inp_hours']

		# marks = predict(hours)
		marks = hours

		return render_template("home.html", intro = my_intro, age = my_age, dog_img = random.choice(dog), marks = marks)


if __name__ == '__main__':
	#to create a web server

	# app.debug = True
	app.run(debug = True)